METUI_FUN['foot_nav_met_28_1'] = {
    name: 'foot_nav_met_28_1',
    weixin: function() {
        var wx = METUI['foot_nav_met_28_1'].find('#met-weixin');
        if (wx.length) {
            Breakpoints.on('xs', {
                enter: function() {
                    if (wx.offset().left < 80) wx.attr({
                        'data-placement': 'right'
                    });
                    if ($(window).width() - wx.offset().left - wx.outerWidth() < 80) wx.attr({
                        'data-placement': 'left'
                    });
                }
            })
            if (wx.data('trigger') == 'click') {
                wx.mouseup(function() {
                    $(this).click();
                });
            }

        }

    },
    collapse: function() {
        Breakpoints.on('xs', {
            enter: function() {
                var list = METUI['foot_nav_met_28_1'].find('.list');
                list.each(function(index, el) {
                    var li = $(this).find('li'),
                        ul = $(this).find('ul'),
                        href = '#' + ul.attr('id');
                    if (li.length > 0) {
                        var cape = $(this).find('.btn-collapse');
                        cape.attr('data-toggle', 'collapse');
                        cape.attr('data-target', href);
                        ul.addClass('collapse');
                        ul.on('show.bs.collapse', function() {
                            $(this).siblings().find('i').css('transform', 'rotate(180deg)');
                        })
                        ul.on('hide.bs.collapse', function() {
                            $(this).siblings().find('i').css('transform', 'rotate(0deg)');
                        })
                    }
                });
            }
        })
    }
};
