<script src="/ffsm/statics/taluo/divine.cdn.h55u.com/AppDivine/guess/script/jquery.min.js"></script>
<script>
    var screen_height = $(window).height();
    if(screen_height>650){
        $('.desc_text').css({'margin-top': '2.5rem', 'height': '2rem'});
    }else{
        $('.desc_text').css({'margin-top': '2.5rem', 'height': '1.8rem'});
        $('.retry-tips').css({'margin-top': '0.2rem'});
    }

    // 生成牌
    for (var c = 0; c < 22; c++) {
        $(".card_list").append("<li class='move_card" + c + "'><img src='/ffsm/statics/taluo/divine.cdn.h55u.com/AppDivine/guess/cover.png?v=1.0' /></li>");
        var step = -52.5+c*5;
        $(".move_card"+c).css({
            '-webkit-transform': 'rotate('+step+'deg)',
            'transform': 'rotate('+step+'deg)',
            '-webkit-animation-delay': c * 0.05 + 's',
            'animation-delay': c * 0.05 + 's'
        });
        if(c==21){
            setTimeout(function () {
                $('.triangle').fadeIn(800);
                setTimeout(function () {
                    $('#user_hint_1').fadeIn(800);
                    setTimeout(function () {
                        $('#user_hint_2').fadeIn(800);
                    },500);
                },400);
            },300);
        }
    }

    // 牌面集合
    var items = [{"image":"<?php echo $this->_tpl_vars['data']['data']['carinfo']['0']['img']; ?>
","name":"<?php echo $this->_tpl_vars['data']['data']['carinfo']['0']['title']; ?>
","positive":"<?php if ($this->_tpl_vars['data']['data']['carinfo']['0']['zf'] == 0): ?>逆位<?php else: ?>正位<?php endif; ?>"},
		{"image":"<?php echo $this->_tpl_vars['data']['data']['carinfo']['1']['img']; ?>
","name":"<?php echo $this->_tpl_vars['data']['data']['carinfo']['1']['title']; ?>
","positive":"<?php if ($this->_tpl_vars['data']['data']['carinfo']['1']['zf'] == 0): ?>逆位<?php else: ?>正位<?php endif; ?>"},
		{"image":"<?php echo $this->_tpl_vars['data']['data']['carinfo']['2']['img']; ?>
","name":"<?php echo $this->_tpl_vars['data']['data']['carinfo']['2']['title']; ?>
","positive":"<?php if ($this->_tpl_vars['data']['data']['carinfo']['2']['zf'] == 0): ?>逆位<?php else: ?>正位<?php endif; ?>"},
		{"image":"<?php echo $this->_tpl_vars['data']['data']['carinfo']['3']['img']; ?>
","name":"<?php echo $this->_tpl_vars['data']['data']['carinfo']['3']['title']; ?>
","positive":"<?php if ($this->_tpl_vars['data']['data']['carinfo']['3']['zf'] == 0): ?>逆位<?php else: ?>正位<?php endif; ?>"}];
    var length = items.length;

    // 牌面象征意义
    var symbol = ["自我剖析", "对方心迹", "关系走向", "塔罗指引"];

    // 选牌统计
    var count = 0;

    // 倒计时
    var num = 0, isEnd = false;
    function startTimer(text) {
        var timer = setInterval(function () {
            num++;
            if (num == 2) {
                isEnd = true;
                $(".user_hint>span").text(text);
                clearInterval(timer);
            }
        }, 600);
    }

    // 选牌过程动画
    function changeCard(li, items, selector, top, left, text, start_point) {
        // 判断用户是否已完成选择
        if (count < length) {
            var item = items[count];
            $(selector + ' .cardName').text(item['name']);
            $(selector + " .directional").text('（'+item['positive']+'）');
            $(selector + " .card_desc").text(symbol[count]);
            $(selector + " .card-wrap>div>.ensure_back").attr("src", item['image']);

            $(selector).css({"top": start_point.y + "rem", "left": start_point.x+ "rem", "margin-left": "-1.1rem", "display": "block", "z-index": 999, "-webkit-transform": "translateZ(100px)","transform": "translateZ(100px)"});

            $(selector).show();

            $(selector).animate({ top: top + "rem", left: left + "%"}, 600, function () {
                $(selector + " .card-wrap .img-wrap").addClass("turn");
                $(selector + " p").animate({opacity: 1}, 800);
            });

            // 隐藏选中牌
            $(li).hide();

            // 再次选牌
            if (count !== length) {
                count = count + 1;
                num = 0;
                isEnd = false;
                startTimer(text);
            }

            if (count == length){
                setTimeout(function () {
                    setTimeout(function () {
                        $(".card_list").fadeOut(200);
                    },100);
                    $(".content").animate({ top: '-6.5rem' }, "slow", function () {
                        setTimeout(function () {
                            $(".desc_text").css({display: 'flex', display: '-webkit-flex'});
                            $(".watch_btn").css({display: 'flex', display: '-webkit-flex'});
                            $('#desc_1').fadeIn(800);
                            setTimeout(function () {
                                $('#desc_2').fadeIn(800);
                                setTimeout(function () {
                                    $(".view_btn").fadeIn(1000);
                                    setTimeout(function () {
                                        $('.retry-tips').fadeIn(400);
                                    }, 400)
                                },400);
                            },400);
                        },100);
                    });
                }, (1200));
            }
        }
    }

    // 选牌
    $(".card_list>li").each(function () {
        var _this = $(this);
        $(this).click(function (e) {
            var empty = (clientWidthTrue-clientWidth)/2;
            var start_point = {x: (e.clientX-empty)/value, y:e.clientY/value};
            if (count == 0) {
                changeCard(_this, items, ".card_0", 6.8, 50, "2", start_point);
            } else if (count == 1) {
                if (isEnd == true) {
                    changeCard(_this, items, ".card_1", 10.5, 16, "3", start_point);
                }
            } else if (count == 2) {
                if (isEnd == true) {
                    changeCard(_this, items, ".card_2", 10.5, 84, "4", start_point);
                }
            } else{
                if (isEnd == true) {
                    changeCard(_this, items, ".card_3", 14.2, 50.5, "4", start_point);
                }
            }
        });
    });

    // 查看结果
    $('.view_btn').click(function(){
        try{
            MtaH5.clickStat('taxinlideren',{'lijijiepai':'true'});
        }catch(err){
            console.log(err)
        }
        // window.location.href = "/divine.php/home/example/guesspay/record_id/3779986";
        window.location.href = "/?ac=taluoxinli&oid=<?php echo $this->_tpl_vars['data']['oid']; ?>
&pay=1";
    });
    // 重新选牌
    $('.retry-tips').click(function () {
        // window.location.href = "/divine.php/home/example/guesscard/record_id/3779986/update/1";
        window.location.href = "/?ac=taluoxinli&oid=<?php echo $this->_tpl_vars['data']['oid']; ?>
&update=1";
    });
</script>



