    // 生成牌
    for (var c = 0; c < 22; c++) {
        $(".card_list").append("<li class='move_card" + c + "'><img src='/ffsm/statics/taluo/divine.cdn.h55u.com/AppDivine/fortune/card.png?v=1.1' /></li>");
