                    },300);
                },300);
            },200);
        }
    }
    //  接值数组
    var all = [];
    // 倒计时
    var num = 0,
        isEnd = false;
    function startTimer() {
        var timer = setInterval(function () {
            num++;
            if (num == 2) {
                isEnd = true;
                clearInterval(timer);
            };
        }, 600);
    };

    function changeCard(li, arr, obj, top_val, left_val, start_point, ctx) {
        // 判断用户是否已经选择
        if (all.length < 6) {
            var idx = all.length;
            var item = arr[idx];
            $(obj + " .introCard").text(ctx);
            $(obj + ">div>.ensure_back").attr("src", item['image']);
            all.push(item);
            $(obj).css({"top": start_point.y + "rem", "left": start_point.x+ "rem", "width":"2.2rem", "margin-left": "-1.1rem", "display": "block", "z-index": 9999, "transform": "translateZ(100px)","-webkit-transform": "translateZ(100px)"});
            $(obj).show();
            var position = { top: top_val + "rem", left: left_val + "%"};
            $(obj).animate(position, 700, function () {
                $(obj + ">div").addClass("turn");
                $(obj + " p").animate({opacity: 1}, 700);
            });
            // 再次选牌
            if (all.length !== 7) {
                // 隐藏选中牌及牌列表
                $(li).hide();
                num = 0;
                isEnd = false;
                startTimer();
            };
            // 如果是第六次选牌后
            if (all.length == 6) {
                // 相关显示
                setTimeout(function () {
                    setTimeout(function () {
                        $(".card_list").fadeOut(200);
                        $(".indicate").fadeOut(200);
                    },100);
                    $(".main-content").animate({ top: '-5.6rem' }, "1200", function () {
                        setTimeout(function () {
                            $(".desc_text").css({display: 'flex', display: '-webkit-flex'});
                            $(".watch_btn").css({display: 'flex', display: '-webkit-flex'});
                            $('#desc_1').fadeIn(800);
                            setTimeout(function () {
                                $('#desc_2').fadeIn(800);
                                setTimeout(function () {
                                    $(".view_img").fadeIn(1000);
                                    setTimeout(function () {
                                        $('.retry-tips').fadeIn(400);
                                    }, 400)
                                },400)
                            },400)
                        },100)
                    });
                }, 1200)

            };
        };
    };
    var tarot_sign = ["本命姻缘", "感情状况", "桃花分析", "转运时间", "潜在问题", "塔罗指引"];
    // 选牌
    $(".card_list>li").each(function () {
        var _this = $(this);
        $(this).click(function (e) {
            var empty = (clientWidthTrue-clientWidth)/2;
            var start_point = {x: (e.clientX-empty)/value, y:e.clientY/value};
            if (all.length == 0) {
                changeCard(_this, tarots, ".card_0", 6.2, 30, start_point, tarot_sign[0]);
            } else if (all.length == 1) {
                // 判断动画是否执行完毕
                if (isEnd == true) {
                    changeCard(_this, tarots, ".card_1", 6.2, 100-30, start_point, tarot_sign[1]);
                }
            } else if (all.length == 2) {
                if (isEnd == true) {
                    changeCard(_this, tarots, ".card_2", 10.7, 10.5, start_point, tarot_sign[2]);
                }
            } else if (all.length == 3) {
                if (isEnd == true) {
                    changeCard(_this, tarots, ".card_3", 10.7, 100-10.5, start_point, tarot_sign[3]);
                }
            } else if (all.length == 4) {
                if (isEnd == true) {
                    changeCard(_this, tarots, ".card_4", 15.2, 30, start_point, tarot_sign[4]);
                }
            } else if (all.length == 5) {
                if (isEnd == true) {
                    changeCard(_this, tarots, ".card_5", 15.2, 100-30, start_point, tarot_sign[5]);
                }
            } else {
                console.log(all);
            }
        });
    });

